from tkinter import *root = Tk()root.geometry("500x500")root.title("colors")import randomdictionary = {"Colors" : ["maroon1","lawn green","magenta2","purple1","springgreen2","chocolate1", "deep pink","cyan"]}def colorrrr():    randomnum = random.randint(0 , 7)    root.configure(bg = dictionary["Colors"][randomnum])button = Button(root , text = "Click me" , command = colorrrr)button.pack()root.mainloop()